INSERT INTO `#__kunena_messages_text` (`mesid`, `message`) VALUES
(1, '[size=4][b]Welcome to Kunena![/b][/size] \n\n Thank you for choosing Kunena for your community forum needs in Joomla. \n\n Kunena, translated from Swahili meaning “to speak”, is built by a team of open source professionals with the goal of providing a top quality, tightly unified forum solution for Joomla. \n\n\n [size=4][b]Additional Kunena Resources[/b][/size] \n\n [b]Kunena Documentation:[/b] [url]http://www.kunena.org/docs[/url] \n\n [b]Kunena Support Forum[/b]: [url]http://www.kunena.org/forum[/url] \n\n [b]Kunena Downloads:[/b] [url]http://www.kunena.org/download[/url] \n\n [b]Kunena Blog:[/b] [url]http://www.kunena.org/blog[/url] \n\n [b]Follow Kunena on Twitter:[/b] [url]http://www.kunena.org/twitter[/url]'),
(2, 'This category is only for reporting defects with K 3.0. Topics about other versions of Kunena will be deleted.\r\n\r\nDo not use this category:\r\nto ask questions about K 3.0 or to ask when a new version of K 3.0 will be released;\r\nto ask about other (older) versions of Kunena; or\r\nif you have tried to install K 3.0 on J! 1.5; or\r\nif you installed K 3.0 on a live, production site and you want your site restored to its previous state; or\r\nif this website (www.kunena.org) works but works differently to how you expected\r\n\r\nYou must include your K 3.0 configuration report; if you do not include your configuration report, your topic may be closed (locked) or deleted without any further warnings from the moderators.\r\n\r\nTopics that have been closed (resolved) will be archived and no further discussion on those topics will be allowed.'),
(3, 'for requests to add or remove the standard features of Kunena;\r\nfor questions commonly asked or "how to" in nature (see the FAQs menu tab above);\r\nfor help with Kunena versions that are not the latest stable release; or\r\nfor general Joomla or website administration matters'),
(4, '[quote="admin" post=1][size=4][b]Welcome to Kunena![/b][/size] \r\n\r\n Thank you for choosing Kunena for your community forum needs in Joomla. \r\n\r\n Kunena, translated from Swahili meaning “to speak”, is built by a team of open source professionals with the goal of providing a top quality, tightly unified forum solution for Joomla. \r\n\r\n\r\n [size=4][b]Additional Kunena Resources[/b][/size] \r\n\r\n [b]Kunena Documentation:[/b] [url]http://www.kunena.org/docs[/url] \r\n\r\n [b]Kunena Support Forum[/b]: [url]http://www.kunena.org/forum[/url] \r\n\r\n [b]Kunena Downloads:[/b] [url]http://www.kunena.org/download[/url] \r\n\r\n [b]Kunena Blog:[/b] [url]http://www.kunena.org/blog[/url] \r\n\r\n [b]Follow Kunena on Twitter:[/b] [url]http://www.kunena.org/twitter[/url][/quote]\r\nfor requests to add or remove the standard features of Kunena;\r\nfor questions commonly asked or "how to" in nature (see the FAQs menu tab above);\r\nfor help with Kunena versions that are not the latest stable release; or\r\nfor general Joomla or website administration matters  :silly:  :silly:  :silly:  :silly:'),
(5, 'B)  B)  B)  B) Choose from one of the following sub-categories. Topics not related to a currently supported version of Kunena will be locked and/or archived. If you are unsure what is the current supported version of Kunena, please go to the download page.  :woohoo:'),
(6, 'Do not use this category:'),
(7, 'Kunena speaks your language so check out our localised categories below.\r\n\r\nSome of the following categories may be locked because there is no-one to moderate the topics. We apologise for any inconvenience that this causes.\r\n\r\nIf you feel that you have the skill and the time to moderate any of these areas, please apply to join our moderation team (details are on our team page).'),
(8, 'go to the download page'),
(9, '[quote="anna" post=5]B)  B)  B)  B) Choose from one of the following sub-categories. Topics not related to a currently supported version of Kunena will be locked and/or archived. If you are unsure what is the current supported version of Kunena, please go to the download page.  :woohoo:[/quote]\r\ngo to the download page. thanks thanks :evil:  :evil:'),
(10, 'Kunena is the ideal forum extension for Joomla. It\'s free, fully integrated, and no bridges or hacks are required.'),
(11, 'This category is only for reporting defects with K 2.0\r\n\r\n :cheer:  :cheer: Do not use this category:\r\nif this website (www.kunena.org) works but works differently to how you expected\r\nfor requests to add or remove the standard features of Kunena;\r\nfor questions commonly asked or "how to" in nature (see the FAQs menu tab above);\r\nfor help with Kunena versions that are not the latest stable release; or\r\nfor general Joomla or website administration matters\r\n\r\nYou must include your K 2.0 configuration report; if you do not include your configuration report, your topic may be closed (locked) or deleted without any further warnings from the moderators.\r\n\r\nTopics that have been closed (resolved) will be archived and no further discussion on those topics will be allowed.'),
(12, 'Topics that have been closed'),
(13, 'This is the forum\'s e-mail address. If left empty, Kunena will use the default address from Joomla. Make this a valid e-mail address since all subscriptions and moderator notifications are sent from it.'),
(14, 'Rules for posting in K 3.0 Support category\r\nRules for posting in K 3.0 Support category\r\nRules for posting in K 3.0 Support category\r\nRules for posting in K 3.0 Support category'),
(15, 'We encourage everyone to subscribe to this category to receive important news about new releases, etc. by email.  :lol:  :lol:'),
